import React, { useState } from 'react';
import FacebookLogin from 'react-facebook-login';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const responseFacebook = (response) => {
    // Aquí obtienes el token de acceso de Facebook
    console.log(response.accessToken);
    // Puedes utilizar el token de acceso para realizar llamadas adicionales a la API de Facebook
    // o para autenticar al usuario en tu propio sistema
  };

  const handleInputChange = (event) => {
    if (event.target.name === 'username') {
      setUsername(event.target.value);
    } else if (event.target.name === 'password') {
      setPassword(event.target.value);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Realiza cualquier validación adicional del formulario aquí antes de enviar la solicitud
    // ...

    // Envía la solicitud de inicio de sesión a tu propio sistema aquí
    // ...
  };

  return (
    <div>
      <h2>Iniciar sesión con Facebook</h2>
      <FacebookLogin
        appId="TU_APP_ID_DE_FACEBOOK"
        autoLoad={false}
        fields="name,email,picture"
        callback={responseFacebook}
      />

      <h2>Iniciar sesión con usuario y contraseña</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Usuario:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="password">Contraseña:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit">Iniciar sesión</button>
      </form>
    </div>
  );
};

export default LoginForm;
