import React, { useState } from 'react';
import FacebookLogin from 'react-facebook-login';

const Lista = () => {
  const responseFacebook = (response) => {
    // Aquí obtienes el token de acceso de Facebook
    console.log(response.accessToken);
    // Puedes utilizar el token de acceso para realizar llamadas adicionales a la API de Facebook
    // o para autenticar al usuario en tu propio sistema
  };

  return (
    <div>
      <h2>Iniciar sesión con Facebook</h2>
      <FacebookLogin
        appId="1231787427472410"
        autoLoad={false}
        fields="name,email,picture"
        callback={responseFacebook}
      />
    </div>
  );
};

export default Lista;
