import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';
import PlacesAutocomplete, {
  geocodeByAddress,
  getLatLng
} from 'react-google-places-autocomplete';


const Lista = () => {
  const alumnosInicial = [];

  const [alumnos, setAlumnos] = useState(alumnosInicial);
  const [foto, setFoto] = useState(null);
  const [direccion, setDireccion] = useState(null);

  const agegarAlumno = (event) => {
    event.preventDefault();
    const nuevoAlumno = document.getElementById('matricula').value;
    const nuevoNombre = document.getElementById('nombre').value;
    const nuevaCarrera = document.getElementById('carrera').value;

    if (foto && nuevoAlumno !== '' && nuevoNombre !== '' && nuevaCarrera !== 'S/CARRERA' && direccion) {
      setAlumnos([
        ...alumnos,
        {
          id: nuevoAlumno,
          nombre: nuevoNombre,
          carrera: nuevaCarrera,
          foto: foto,
          ubicacion: direccion
        }
      ]);
    } else {
      alert('¡ERROR! CAMPOS VACÍOS');
    }

    document.getElementById('matricula').value = '';
    document.getElementById('nombre').value = '';
    document.getElementById('carrera').selectedIndex = 0;
    setFoto(null);
    setDireccion(null);
  };

  const handleSelect = async (address) => {
    try {
      const results = await geocodeByAddress(address);
      const latLng = await getLatLng(results[0]);
      setDireccion({
        address: address.label,
        latitude: latLng.lat,
        longitude: latLng.lng
      });
    } catch (error) {
      console.error('Error al obtener la ubicación:', error);
    }
  };

  const editarAlumno = (id, nuevoNombre, nuevaFoto) => {
    const alumnoEditar = alumnos.find((alumno) => alumno.id === id);

    if (nuevaFoto) {
      setAlumnos(
        alumnos.map((alumno) => {
          if (alumno.id === id) {
            return {
              ...alumno,
              nombre: nuevoNombre,
              foto: nuevaFoto,
              ubicacion: direccion
            };
          } else {
            return alumno;
          }
        })
      );
    } else {
      setAlumnos(
        alumnos.map((alumno) => {
          if (alumno.id === id) {
            return {
              ...alumno,
              nombre: nuevoNombre,
              ubicacion: direccion
            };
          } else {
            return alumno;
          }
        })
      );
    }

    document.getElementById('matricula').value = '';
    document.getElementById('nombre').value = '';
    document.getElementById('carrera').selectedIndex = 0;
    setFoto(null);
    setDireccion(null);
  };

  const eliminarAlumno = (id) => {
    const confirmarEliminar = window.confirm('¿Estás seguro de eliminar este alumno?');
    if (confirmarEliminar) {
      const nuevosAlumnos = alumnos.filter((alumno) => alumno.id !== id);
      setAlumnos(nuevosAlumnos);
    }
  };

  return (
    <>
      <h2 className='text-center'>Lista de Alumnos</h2>

      <table className='table text-center'>
        <thead>
          <tr>
            <th>Matricula</th>
            <th>Nombre</th>
            <th>Carrera</th>
            <th>Foto</th>
            <th>Ubicación</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {alumnos.map((alumno, index) => (
            <tr key={index}>
              <td>{alumno.id}</td>
              <td>{alumno.nombre}</td>
              <td>{alumno.carrera}</td>
              <td>{alumno.foto && <img src={URL.createObjectURL(alumno.foto)} alt={alumno.nombre} width='50' />}</td>
              <td>{alumno.ubicacion && alumno.ubicacion.address}</td>
              <td>
                <button className='btn btn-success' onClick={() => editarAlumno(alumno.id, alumno.nombre, alumno.foto)}>
                  Editar
                </button>
                &nbsp;&nbsp;
                <button className='btn btn-danger' onClick={() => eliminarAlumno(alumno.id)}>
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className='container my-5 contcom'>
        <div className='input-group mb-3'>
          <form>
            <span className='input-group-text'>MATRICULA</span>
            <input id='matricula' className='form-control' type='text' />
            <span className='input-group-text'>NOMBRE</span>
            <input id='nombre' className='form-control' type='text' />
            <span className='input-group-text'>CARRERA</span>
            <select className='form-control' name='carrera' id='carrera'>
              <option value='S/CARRERA'>--ELEGIR CARRERA--</option>
              <option value='SISTEMAS COMPUTACIONALES'>SISTEMAS COMPUTACIONALES</option>
              <option value='QUIMICA'>QUIMICA</option>
              <option value='CIVIL'>CIVIL</option>
            </select>

            <br />

            <PlacesAutocomplete
              value={direccion}
              onChange={(address) => setDireccion(address)}
              onSelect={handleSelect}
            >
              {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
                <div>
                  <span className='input-group-text'>UBICACIÓN</span>
                  <input
                    {...getInputProps({
                      placeholder: 'Buscar ubicación...',
                      className: 'form-control'
                    })}
                  />
                  <div>
                    {loading ? <div>Cargando...</div> : null}

                    {suggestions.map((suggestion) => {
                      const style = {
                        backgroundColor: suggestion.active ? '#41b6e6' : '#fff'
                      };

                      return (
                        <div {...getSuggestionItemProps(suggestion, { style })}>
                          {suggestion.description}
                        </div>
                      );
                    })}
                  </div>
                </div>
              )}
            </PlacesAutocomplete>

            <br />
            <input id='foto' className='form-control' type='file' onChange={(e) => setFoto(e.target.files[0])} />

            <br />
            <button className='btn btn-primary btnd' onClick={agegarAlumno}>
              Agregar Alumno
            </button>
          </form>
        </div>
      </div>
    </>
  );
};

export default Lista;
