import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';
import FacebookLogin from 'react-facebook-login';

const Lista = () => {
  const [name, setName] = useState('');
  const [profile, setProfile] = useState(null);

  const responseFacebook = (response) => {
    console.log(response); // Aquí puedes manejar la respuesta de inicio de sesión de Facebook
  };

  const handleInputChange = (event) => {
    setName(event.target.value);
  };

  const handleSearch = () => {
    // Realiza la llamada a la API de búsqueda de Facebook utilizando el nombre ingresado
    // Puedes implementar aquí la lógica para buscar el perfil en Facebook y obtener los datos relevantes
    // Por simplicidad, en este ejemplo simplemente asignamos el nombre ingresado como resultado de búsqueda
    setProfile({
      name,
      picture: responseFacebook.picture.data.url
    });
  };

  return (
    <div>
      <h2>Iniciar sesión con Facebook</h2>
      <FacebookLogin
        appId="TU_CLAVE_DE_API_DE_FACEBOOK"
        autoLoad={false}
        fields="name,email,picture" // Especifica los campos que deseas obtener
        callback={responseFacebook}
      />
      <div>
        <input type="text" value={name} onChange={handleInputChange} placeholder="Ingresa el nombre" />
        <button onClick={handleSearch}>Buscar</button>
      </div>
      {profile && (
        <div>
          <h3>Perfil encontrado:</h3>
          <p>Nombre: {profile.name}</p>
          <img src={profile.picture} alt="Foto de perfil" />
        </div>
      )}
    </div>
  );
};

export default Lista;
