import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css'
import FacebookLogin from 'react-facebook-login';


const Lista = () => {
  const alumnosInicial = [
    
  ];

  const [alumnos, SetAlumnos] = useState(alumnosInicial);
  const [foto, setFoto] = useState(null);

  
  const agegarAlumno = (event) => {
    event.preventDefault();
    
    const nuevoAlumno = document.getElementById('matricula').value;
    const nuevoNombre = document.getElementById('nombre').value;
    const nuevaCarrera = document.getElementById('carrera').value;
    if (foto && nuevoAlumno!='' && nuevoNombre!='' && nuevaCarrera!='S/CARRERA') {
    SetAlumnos([
      ...alumnos,
      {
        id: nuevoAlumno,
        nombre: nuevoNombre,
        carrera: nuevaCarrera,
        foto: foto
      }
    ]);
  }else{
    alert('¡ERROR! CAMPOS VACIOS');
  }

    document.getElementById('matricula').value = '';
    document.getElementById('nombre').value = '';
    document.getElementById('carrera').selectedIndex = 0; 

  };
  

      
  const editarAlumno = (id) => {
    const alumnoEditar = alumnos.find((alumno) => alumno.id === id);
    const nuevoNombre = document.getElementById('nombre').value;
    const nuevaCarrera = document.getElementById('carrera').value;
    const nuevaFoto = foto;
    if (foto) {
      const nuevaFoto = foto;
      SetAlumnos(
        alumnos.map((alumno) => {
          if (alumno.id === id) {
            return {
              ...alumno,
              nombre: alumno.nombre,
              carrera: alumno.carrera,
              foto: nuevaFoto
              
            };
          
          } else {
            return alumno;
          }
          
        })
      );
      document.getElementById('matricula').value = '';
      document.getElementById('nombre').value = '';
      document.getElementById('carrera').selectedIndex = 0; 
    }

    if(nuevaCarrera!='S/CARRERA'){
      
      SetAlumnos(
        alumnos.map((alumno) => {
          if (alumno.id === id) {
            return {
              ...alumno,
              nombre: alumno.nombre,
              carrera: nuevaCarrera,
              foto: alumno.foto
              
            };
          
          } else {
            return alumno;
          }
          
        })
      );
      document.getElementById('matricula').value = '';
      document.getElementById('nombre').value = '';
      document.getElementById('carrera').selectedIndex = 0; 
    }

    if(nuevoNombre!=''){
      SetAlumnos(
        alumnos.map((alumno) => {
          if (alumno.id === id) {
            return {
              ...alumno,
              nombre: nuevoNombre,
              carrera: alumno.carrera,
              foto: alumno.foto
              
            };
          
          } else {
            return alumno;
          }
          
        })
      );
      document.getElementById('matricula').value = '';
      document.getElementById('nombre').value = '';
      document.getElementById('carrera').selectedIndex = 0; 
    }

  };
  
  
  
  
  const eliminarAlumno = (id) => {

    const confirmarEliminar = window.confirm('¿Estás seguro de eliminar este alumno?');
    if (confirmarEliminar) {
      const nuevosAlumnos = alumnos.filter((alumno) => alumno.id !== id);
      SetAlumnos(nuevosAlumnos);
    }
  };

 

  return (
    <>
      <h2 className='text-center'>Lista de Alumnos</h2>

      <table className="table text-center">
        <thead>
          <tr>
            <th>Matricula</th>
            <th>Nombre</th>
            <th>Carrera</th>
            <th>Foto</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {alumnos.map((alumno, index) => (
            <tr key={index}>
              <td>{alumno.id}</td>
              <td>{alumno.nombre}</td>
              <td>{alumno.carrera}</td>
              <td>{alumno.foto && <img src={URL.createObjectURL(alumno.foto)} alt={alumno.nombre} width="50" />}</td>
              <td>
                <button className="btn btn-success" onClick={() => editarAlumno(alumno.id,alumno.nombre,alumno.foto)}>Editar</button>
                &nbsp;&nbsp;
                <button className="btn btn-danger" onClick={() => eliminarAlumno(alumno.id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div class="container my-5 contcom">
      <div className='input-group mb-3'>
      <form>
      <span className='input-group-text'>MATRICULA</span>
      <input id="matricula" class="form-control" type="text"/>
      <span className='input-group-text'>NOMBRE</span>
      <input id="nombre" class="form-control" type="text" />
      <span className='input-group-text'>CARRERA</span>
      <select className='form-control' name="carrera" id="carrera">
      <option value="S/CARRERA">--ELEGIR CARRERA--</option>
        <option value="SISTEMAS COMPUTACIONALES">SISTEMAS COMPUTACIONALES</option>
        <option value="QUIMICA">QUIMICA</option>
        <option value="CIVIL">CIVIL</option>
      </select>
      <br></br>
      <input id="foto" class="form-control" type="file" onChange={(e) => setFoto(e.target.files[0])} />

      <br></br>
      <button className="btn btn-primary btnd" onClick={agegarAlumno}>Agregar Alumno</button>
      </form>
      </div>
      </div>
    </>
  );
          
};

export default Lista;
