import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';
import FacebookLogin from 'react-facebook-login';

const Lista = () => {
  const [name, setName] = useState('');
  const [results, setResults] = useState([]);

  const responseFacebook = (response) => {
    console.log(response); // Aquí puedes manejar la respuesta de inicio de sesión de Facebook
  };

  const handleInputChange = (event) => {
    setName(event.target.value);
  };

  const handleSearch = () => {
    // Realiza la llamada a la API de búsqueda de Facebook utilizando el nombre ingresado
    // Aquí debes implementar la lógica para buscar los perfiles en Facebook y obtener los resultados
    // Por simplicidad, en este ejemplo simplemente generamos una lista de resultados falsos
    const fakeResults = [
      { id: 1, name: 'John Doe', picture: 'https://example.com/johndoe.jpg' },
      { id: 2, name: 'Jane Smith', picture: 'https://example.com/janesmith.jpg' },
      { id: 3, name: 'Alice Johnson', picture: 'https://example.com/alicejohnson.jpg' }
    ];
    setResults(fakeResults);
  };

  return (
    <div>
      <h2>Iniciar sesión con Facebook</h2>
      <FacebookLogin
        appId="TU_CLAVE_DE_API_DE_FACEBOOK"
        autoLoad={false}
        fields="name,email,picture" // Especifica los campos que deseas obtener
        callback={responseFacebook}
      />
      <div>
        <input type="text" value={name} onChange={handleInputChange} placeholder="Ingresa el nombre" />
        <button onClick={handleSearch}>Buscar</button>
      </div>
      {results.length > 0 && (
        <div>
          <h3>Resultados:</h3>
          <ul>
            {results.map((profile) => (
              <li key={profile.id}>
                <p>Nombre: {profile.name}</p>
                <img src={profile.picture} alt="Foto de perfil" />
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Lista;
