import React, { useState } from 'react';
import axios from 'axios';

const Lista = () => {
  const [searchName, setSearchName] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (event) => {
    setSearchName(event.target.value);
  };

  const searchProfilesOnFacebook = async () => {
    try {
      const accessToken = 'TU_TOKEN_DE_ACCESO'; // Reemplaza con tu token de acceso válido
      const url = `https://graph.facebook.com/v12.0/search?q=${encodeURIComponent(
        searchName
      )}&type=user&fields=name,picture&access_token=${accessToken}`;

      const response = await axios.get(url);
      const { data } = response;

      setSearchResults(data.data);
    } catch (error) {
      console.error('Error al realizar la búsqueda en Facebook:', error);
    }
  };

  return (
    <div>
      <h2>Buscar perfiles en Facebook</h2>
      <input type="text" value={searchName} onChange={handleInputChange} />
      <button onClick={searchProfilesOnFacebook}>Buscar</button>
      <ul>
        {searchResults.map((profile) => (
          <li key={profile.id}>
            <div>
              <img src={profile.picture.data.url} alt={profile.name} />
              <p>{profile.name}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Lista;
